import {useState} from "react";

const styledContainer = {
    display: "flex",
    gap: "10px",
    alignItems: "center"
}

const styledStarContainer = {
    display: "flex",
    gap: "8px"
}

const styledText = {
    fontSize: "20px",
    color: "black"
}
export default function StarRating({maxRating = 5}) {
    const [rating, setRating] = useState(0)
    const [tempRating, setTempRating] = useState(0)

    function handleClick(i) {
        setRating(i)
    }

    function handleMouseOn() {

    }
    return (<div style={styledContainer}>
            <div style={styledStarContainer}>
                {Array.from({length: maxRating}, (_, i) =>
                    (<Star key={i} onClick={() => handleClick(i + 1)}/>
                    ))}
            </div>
        <p style={styledText}>{rating ? rating: ''}</p>
        </div>)
}

function Star({onClick, tempRating}) {
    return (
        <span role={"button"}
              style={{width: '30px', height: '30px', cursor: "pointer"}}
              onClick={onClick}
        >
        <svg
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 20 20"
            fill="#000"
            stroke="#000"
        >
            <path
                d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"
            />
        </svg>
        </span>
    )
}

